class BasicRequestService {
  constructor() {
    this.requestMethod = require('umi-request');
  }

  async download(url) {
    const xhr = new XMLHttpRequest();
    return new Promise((resolve, reject) => {
      if (!this._hasPermission()) {
        reject(new Error('Permission denied for downloading the file.'));
        return;
      }

      xhr.open('GET', url, true);
      xhr.responseType = 'blob';

      xhr.onload = function () {
        if (xhr.status >= 200 && xhr.status < 300) {
          resolve(xhr.response);
        } else {
          reject(new Error(`Download failed with status: ${xhr.status}`));
        }
      };

      xhr.onerror = function () {
        reject(new Error('Network error occurred during download.'));
      };

      xhr.send();
    });
  }

  _hasPermission() {
    // Implement actual permission check logic here
    return true;
  }

  async request(url, options) {
    const { method, headers = {}, data, requestType } = options;

    const config = {
      url,
      method,
      headers,
    };

    if (method.toLowerCase() === 'post') {
      config.headers['Content-Type'] = requestType || 'application/json';
      config.data = data;
    } else if (method.toLowerCase() !== 'get' && method.toLowerCase() !== 'put') {
      throw new Error('Unsupported HTTP method');
    }

    if (data && method.toLowerCase() !== 'post') {
      config.params = data;
    }

    return this.requestMethod(config);
  }
}
