  }
}

class Response {
  constructor(text, { status, statusText, headers, url }) {
    this._text = text;
    this._blob = null;
    this._json = null;
    this._headers = headers;
    this._url = url;
    this._status = status;
    this._statusText = statusText;
  }

  get ok() {
    return this.status >= 200 && this.status < 300;
  }

  get statusText() {
    return this._statusText;
  }

  get status() {
    return this._status;
  }

  get url() {
    return this._url;
  }

  text() {
    return Promise.resolve(this._text);
  }

  json() {
    if (this._json) return Promise.resolve(this._json);
    return this.text().then(JSON.parse).catch(() => {
      throw new Error("Invalid JSON response");
    });
  }

  blob() {
    if (this._blob) return Promise.resolve(this._blob);
    return new Promise((resolve, reject) => {
      try {
        const b = new Blob([this._text]);
        this._blob = b;
        resolve(b);
      } catch (e) {
        reject(e);
      }
    });
  }

  clone() {
    return new Response(
      this._text,
      {
        status: this._status,
        statusText: this._statusText,
        headers: { ...this._headers },
        url: this._url,
      },
      this._blob
    );
  }

  get headers() {
    return {
      keys: () => Object.keys(this._headers),
      entries: () => Object.entries(this._headers),
      get: (name) => this._headers[name],
      has: (name) => name in this._headers,
    };
  }
}
