  const PORT = 3000;

  // Serve static files from the 'public' directory
  app.use(express.static(path.join(__dirname, 'public')));

  // Middleware to parse JSON and URL-encoded form data
  app.use(bodyParser.json());
  app.use(bodyParser.urlencoded({ extended: true }));

  // Route to render the main HTML page
  app.get('/', (req, res) => {
    res.sendFile(path.join(__dirname, 'views', 'index.html'));
  });

  // Route to handle PDF generation
  app.post('/generate-pdf', (req, res) => {
    const htmlContent = req.body.html;

    // Save HTML content to a temporary file or pass directly to command
    const tempHtmlFile = path.join(__dirname, 'temp.html');
    const pdfOutputFile = path.join(__dirname, 'output.pdf');

    // Write HTML content to a temporary file
    require('fs').writeFileSync(tempHtmlFile, htmlContent);

    // Command to convert HTML to PDF using wkhtmltopdf
    const command = `wkhtmltopdf ${tempHtmlFile} ${pdfOutputFile}`;

    exec(command, (error, stdout, stderr) => {
      if (error) {
        console.error(`Error executing command: ${error.message}`);
        return res.status(500).send('PDF generation failed.');
      }

      if (stderr) {
        console.error(`Command stderr: ${stderr}`);
        return res.status(500).send('PDF generation failed.');
      }

      // Send the generated PDF as a response
      res.download(pdfOutputFile, 'generated.pdf', (err) => {
        if (err) {
          console.error(`Download error: ${err.message}`);
        }
        // Clean up temporary files
        require('fs').unlinkSync(tempHtmlFile);
        require('fs').unlinkSync(pdfOutputFile);
      });
    });
  });

  // Start the server
  app.listen(PORT, () => {
    console.log(`Server is running on http://localhost:${PORT}`);
  });
}
