    async.eachSeries(req.body.events, (event, callback) => {
      locker.acquire(`event:${event.id}`, () => {
        util.processEvent(event, (err) => {
          locker.release(`event:${event.id}`);
          callback(err);
        });
      });
    }, (err) => {
      if (err) return res.status(500).json({ error: err.message });
      res.send('Events processed');
    });
