    server.get('*', (req, res) => {
      const url = URL_MAP[req.path];
      if (url) {
        app.render(req, res, url);
      } else {
        handle(req, res);
      }
    });

    server.listen(port, (err) => {
      if (err) throw err;
      console.log(`> Ready on ${ROOT_URL}`);
    });
  });
}

