function setupExpressServer() {
  const app = express();
  const corsOptions = {
    origin: 'http://example.com',
    methods: ['GET'],
    allowedHeaders: ['Content-Type', 'Authorization']
  };
  app.use(cors(corsOptions));

  app.get('/wikipedia_summaries', async (req, res) => {
    const keyword = req.query.keyword;
    try {
      const searchResponse = await axios.get('https://en.wikipedia.org/w/api.php', {
        params: {
          action: 'query',
          list: 'search',
          srsearch: keyword,
          format: 'json'
        }
      });
      const results = searchResponse.data.query.search;
      const summaries = [];
      for (const item of results) {
        const pageId = item.pageid;
        const summaryResponse = await axios.get('https://en.wikipedia.org/w/api.php', {
          params: {
            action: 'query',
            pageids: pageId,
            prop: 'extracts|categories',
            format: 'json'
          }
        });
        const data = summaryResponse.data.query.pages[pageId];
        summaries.push({
          title: item.title,
          summary: data.extract,
          categories: data.categories || []
        });
      }
      res.json(summaries);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch Wikipedia data' });
    }
  });

