function checkData(receive, password, commandHandler) {
  const dataStr = receive.data.toString();
  try {
    const parsedData = JSON.parse(dataStr);
    const code = parsedData.code;
    const timestamp = parsedData.timestamp;
    const expectedCode = crypto.createHash('md5').update(password + timestamp).digest('hex');
    if (code.toString() !== expectedCode) {
      receive.socket.write('Invalid code');
      return;
    }
    const isValid = checkCode(receive.data, password, code);
    if (!isValid) {
      receive.socket.write('Authentication failed');
      return;
    }
    commandHandler(parsedData.data, code);
  } catch (e) {
    receive.socket.write('Invalid data format');
  }
}

function checkCode(data, password, code) {
  const dataStr = data.toString();
  try {
    const parsedData = JSON.parse(dataStr);
    const timestamp = parsedData.timestamp;
    const expectedCode = crypto.createHash('md5').update(password + timestamp).digest('hex');
    return code.toString() === expectedCode;
  } catch (e) {
    return false;
  }
}

function pack(data, code) {
  const timestamp = Date.now();
  const payload = JSON.stringify({ code, data, timestamp });
  const hash = crypto.createHash('md5').update(payload).digest('hex');
  return Buffer.from(payload + hash);
}

