        eventTarget.addEventListener('open', (event) => { this.onopen(event); });
        eventTarget.addEventListener('close', (event) => { this.onclose(event); });
        eventTarget.addEventListener('connecting', (event) => { this.onconnecting(event); });
        eventTarget.addEventListener('message', (event) => { this.onmessage(event); });
        eventTarget.addEventListener('error', (event) => { this.onerror(event); });

        this.addEventListener = eventTarget.addEventListener.bind(eventTarget);
        this.removeEventListener = eventTarget.removeEventListener.bind(eventTarget);
        this.dispatchEvent = eventTarget.dispatchEvent.bind(eventTarget);

        this.open = function(wasReconnect) {
            var ws = new WebSocket(this.url, protocols || []);
            if (!wasReconnect) {
                eventTarget.dispatchEvent(this.createEvent('connecting'));
            }

            var timeout = setTimeout(() => {
                timedOut = true;
                ws.close();
                timedOut = false;
            }, this.timeoutInterval);

            ws.onopen = () => {
                clearTimeout(timeout);
                this.protocol = ws.protocol;
                this.readyState = WebSocket.OPEN;
