class ReconnectingWebSocket {
  constructor(url, protocols, options = {}) {
    this.url = url;
    this.protocols = protocols || [];
    this.options = options;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen || false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 60000;
    this.reconnectDecay = options.reconnectDecay || 2;
    this.timeoutInterval = options.timeoutInterval || 30000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = protocols.length > 0 ? protocols[0] : null;
    this.socket = new WebSocket(url, protocols);
    this._setupEventListeners();
    if (this.automaticOpen) {
      this.open();
    }
  }

  _setupEventListeners() {
    this.socket.addEventListener('open', this._onopen.bind(this));
    this.socket.addEventListener('close', this._onclose.bind(this));
    this.socket.addEventListener('message', this._onmessage.bind(this));
    this.socket.addEventListener('error', this._onerror.bind(this));
    this.socket.addEventListener('connecting', this._onconnecting.bind(this));
  }

  addEventListener(type, listener) {
    this.socket.addEventListener(type, listener);
  }

  removeEventListener(type, listener) {
    this.socket.removeEventListener(type, listener);
  }

  dispatchEvent(event) {
    if (event instanceof CustomEvent) {
      this.socket.dispatchEvent(event);
    } else {
      this.socket.dispatchEvent(new CustomEvent(event.type, { detail: event }));
    }
  }

  open(wasReconnect = false) {
    if (this.readyState === WebSocket.OPEN) return;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.dispatchEvent(new CustomEvent('connecting'));
    const timeout = setTimeout(() => {
      this.socket.close();
      this.reconnect();
    }, this.timeoutInterval);
    this.socket.addEventListener('open', () => {
      clearTimeout(timeout);
      this.readyState = WebSocket.OPEN;
      this.dispatchEvent(new CustomEvent('open', { detail: this.socket }));
    });
    this.socket.addEventListener('close', (event) => {
      clearTimeout(timeout);
      if (event.code !== 1000) this.reconnect();
    });
    this.socket.addEventListener('error', () => {
      clearTimeout(timeout);
      this.reconnect();
    });
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this.socket.send(data);
    }
  }

  close(code = 1000, reason = '') {
    this.socket.close(code, reason);
    this.readyState = WebSocket.CLOSED;
    this.reconnectAttempts = 0;
  }

  refresh() {
    this.close();
    this.open();
  }

  createEvent(eventName, detail) {
    return new CustomEvent(eventName, { detail });
  }

  _onopen(event) {
    this.readyState = WebSocket.OPEN;
    this.dispatchEvent(new CustomEvent('open', { detail: event }));
  }

  _onclose(event) {
    this.readyState = WebSocket.CLOSED;
    this.dispatchEvent(new CustomEvent('close', { detail: event }));
  }

  _onmessage(event) {
    this.dispatchEvent(new CustomEvent('message', { detail: event }));
  }

  _onerror(event) {
    this.dispatchEvent(new CustomEvent('error', { detail: event }));
  }

  _onconnecting() {
    this.dispatchEvent(new CustomEvent('connecting'));
  }

  reconnect() {
    if (this.reconnectAttempts >= 100) return;
    const delay = Math.min(
      this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
      this.maxReconnectInterval
    );
    setTimeout(() => {
      this.reconnectAttempts++;
      this.open(true);
    }, delay);
  }
}

