    const batch = requests.map(req => {
      const method = req.method;
      const params = req.params;
      const id = this._generateId();
      return this._formatData(method, params, id);
    });
    this._send(batch);
    this._handles[batch[0].id] = { success: successCallback, error: errorCallback };
  }

