    const methodName = this._namespace + method;
    return {
      jsonrpc: '2.0',
      method: methodName,
      params: params || [],
      id: id
    };
  }

  _send(data) {
    if (this._socket.readyState === WebSocket.OPEN) {
      this._socket.send(JSON.stringify(data));
    } else {
      this._reconnect();
      this._socket.send(JSON.stringify(data));
    }
