class ReconnectingWebSocket {
  constructor(url, protocols, options = {}) {
    this.url = url;
    this.protocols = protocols || [];
    this.debug = options.debug !== false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;

    this.ws = new WebSocket(url, protocols);
    this.setupEventListeners();
    if (this.automaticOpen) {
      this.open(false);
    }
  }

  setupEventListeners() {
    this.ws.addEventListener('open', this.onOpen.bind(this));
    this.ws.addEventListener('message', this.onMessage.bind(this));
    this.ws.addEventListener('error', this.onError.bind(this));
    this.ws.addEventListener('close', this.onClose.bind(this));
  }

  onOpen() {
    this.readyState = WebSocket.OPEN;
    this.protocol = this.ws.protocol;
    this.onopen();
  }

  onMessage(event) {
    this.onmessage(event);
  }

  onError(event) {
    this.readyState = WebSocket.CLOSED;
    this.onerror(event);
    this.handleConnectionClose(event);
  }

  onClose(event) {
    this.readyState = WebSocket.CLOSED;
    this.onclose(event);
    this.handleConnectionClose(event);
  }

  handleConnectionClose(event) {
    if (event.reason) {
      this.reconnect();
    } else {
      this.reconnect();
    }
  }

  reconnect() {
    const delay = Math.min(
      this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
      this.maxReconnectInterval
    );
    setTimeout(() => {
      this.reconnectAttempts++;
      this.close();
      this.open(true);
    }, delay);
  }

  close(code, reason) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.close(code, reason);
    }
  }

  refresh() {
    this.close();
    this.open(true);
  }

  open(reconnecting) {
    if (this.ws.readyState === WebSocket.OPEN) {
      return;
    }
    this.ws = new WebSocket(this.url, this.protocols);
    this.setupEventListeners();
    this.readyState = WebSocket.CONNECTING;
    this.dispatchEvent(new Event('connecting'));
    this.onconnecting();
    const timeout = setTimeout(() => {
      this.close();
      this.reconnect();
    }, this.timeoutInterval);
    this.ws.addEventListener('open', () => clearTimeout(timeout));
  }

  send(data) {
    if (this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(data);
    }
  }

  addEventListener(type, listener) {
    this.ws.addEventListener(type, listener);
  }

  removeEventListener(type, listener) {
    this.ws.removeEventListener(type, listener);
  }

  dispatchEvent(event) {
    if (event.type === 'open') {
      this.onopen(event);
    } else if (event.type === 'close') {
      this.onclose(event);
    } else if (event.type === 'error') {
      this.onerror(event);
    } else if (event.type === 'message') {
      this.onmessage(event);
    } else if (event.type === 'connecting') {
      this.onconnecting(event);
    }
  }

  onopen() {}
  onclose() {}
  onconnecting() {}
  onmessage() {}
  onerror() {}
}

