    return 'socket-' + Math.random().toString(36).substr(2, 9);
  }

  ping(socketId) {
    return new Promise((resolve, reject) => {
      const socket = this.webSockets[socketId];
      if (!socket || socket.readyState !== WebSocket.OPEN) {
        reject(new Error('Connection not open'));
        return;
      }
      socket.ping();
      resolve();
    });
  }

  send(socketId, msg, onMessage) {
    return new Promise(async (resolve, reject) => {
      const socket = this.webSockets[socketId];
      if (!socket || socket.readyState !== WebSocket.OPEN) {
        reject(new Error('Connection not open'));
        return;
      }
      try {
        socket.send(JSON.stringify(msg));
        socket.addEventListener('message', (event) => {
          const data = JSON.parse(event.data);
          if (data.type === 2) {
            onMessage(data);
            resolve();
            socket.removeEventListener('message', arguments.callee);
          }
        });
        socket.addEventListener('close', () => {
          reject(new Error('Connection closed'));
        });
      } catch (error) {
        reject(error);
      }
    });
  }

  close(socketId) {
    return new Promise((resolve, reject) => {
      const socket = this.webSockets[socketId];
      if (!socket) {
        resolve();
        return;
      }
      if (socket.readyState === WebSocket.OPEN) {
        socket.close();
      }
      delete this.webSockets[socketId];
      resolve();
    });
  }
}

