  ev: Mitt;
  client: FlyType;
  ws: WebSocket | undefined;

  constructor(_port?: number) {
    this.port = _port || 32333;
    this.token = '';
    this.hwnd = 0;
    this.version = '1.0.0';
    this.ev = new Mitt();
    this.client = new FlyType();
    this.ws = undefined;
  }

  async authorize(): Promise<boolean> {
    try {
      const tokenResponse = await this.client.post('/auth', {});
      this.token = tokenResponse.data.token;

      this.ws = new WebSocket(`ws://localhost:${this.port}`);
      this.ws.onopen = () => {
        this.ws.send(JSON.stringify({ token: this.token }));
      };

      this.ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        this.ev.emit('message', data);
      };

      this.ws.onerror = (error) => {
        console.error('WebSocket error:', error);
      };

      this.ws.onclose = () => {
        console.log('WebSocket closed');
      };

      return true;
    } catch (error) {
      console.error('Authorization failed:', error);
      return false;
    }
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{ status: number, body: any }> {
    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket not open');
    }

    const payload = {
      method,
      path,
      querystring
    };

    this.ws.send(JSON.stringify(payload));

    return new Promise((resolve, reject) => {
      this.ev.on('message', (response) => {
        resolve({ status: response.status, body: response.body });
      });
    });
  }
}

