class AphlictClientServer {
  constructor(server) {
    this._server = server;
    this._lists = {};
    this._adminServers = [];
    this._server.on('request', this._onrequest.bind(this));
  }

  _onrequest(request, response) {
    response.writeHead(501, { 'Content-Type': 'text/plain' });
    response.end('WebSocket server');
  }

  _parseInstanceFromPath(path) {
    const parts = path.split('/');
    for (let i = 1; i < parts.length; i++) {
      if (parts[i] !== 'ws') {
        return parts[i];
      }
    }
    return null;
  }

  listen() {
    this._wsServer = new WebSocket.Server({ server: this._server });
    this._wsServer.on('connection', (socket) => this._onConnection(socket));
  }

  _onConnection(socket) {
    const url = new URL(socket.url);
    const instance = this._parseInstanceFromPath(url.pathname);
    if (!instance) {
      socket.close(400, 'Invalid instance');
      return;
    }

    this._ensureInstance(instance);
    socket.instance = instance;
    socket.on('message', (message) => this._onMessage(socket, message));
    socket.on('pong', () => this._onPong(socket));
    socket.on('close', () => this._onClose(socket, instance));
  }

  _ensureInstance(instance) {
    if (!this._lists[instance]) {
      this._lists[instance] = [];
    }
  }

  _onMessage(socket, message) {
    try {
      const data = JSON.parse(message.toString());
      if (data.command === 'subscribe') {
        this._lists[socket.instance].push(socket);
      } else if (data.command === 'replay') {
        const history = this.getHistory(data.min_age);
        socket.send(JSON.stringify({ history }));
      } else if (data.command === 'ping') {
        socket.send(JSON.stringify({ pong: true }));
      }
    } catch (err) {
      socket.close(400, 'Invalid message format');
    }
  }

  _onPong(socket) {
    this.trace(`Pong received from ${socket.remoteAddress}`);
  }

  _onClose(socket, instance) {
    this.log(`Client disconnected from ${instance}`);
    const listeners = this._lists[instance];
    const index = listeners.indexOf(socket);
    if (index > -1) {
      listeners.splice(index, 1);
