          reject(new Error(`Download failed: ${xhr.status}`));
        }
      };
      xhr.onerror = (e) => {
        reject(e);
      };
      xhr.send();
    });
  }

  request(url, options) {
    if (!['get', 'put', 'post'].includes(options.method)) {
      throw new Error('Unsupported method');
    }
    const config = {
      method: options.method,
      url,
      headers: options.headers,
      data: options.data,
      requestType: options.requestType
    };
    return this.requestMethod.request(config);
  }

  checkPermissions() {
    return true;
  }
