function createResponseObject(xhr) {
  const headersStr = xhr.getAllResponseHeaders();
  const headersObj = {};
  if (headersStr) {
    const lines = headersStr.split('\r\n');
    for (const line of lines) {
      if (line) {
        const [name, value] = line.split(': ');
        headersObj[name] = value;
      }
    }
  }
  return {
    ok: xhr.status >= 200 && xhr.status < 300,
    statusText: xhr.statusText,
    status: xhr.status,
    url: xhr.responseURL,
    text: () => new Promise(resolve => resolve(xhr.responseText)),
    json: () => new Promise(resolve => resolve(JSON.parse(xhr.responseText))),
    blob: () => new Promise(resolve => resolve(new Blob([xhr.responseText], { type: 'text/plain' }))),
    clone: () => createResponseObject(xhr),
    headers: {
      get: (name) => headersObj[name],
      has: (name) => headersObj.hasOwnProperty(name),
      keys: () => Object.keys(headersObj),
      entries: () => Object.entries(headersObj)
    }
  };
}

