const express = require('express');
const path = require('path');

class FOAAS {
  constructor(options) {
    this.app = express();
    this.app.use(express.json());
    this.app.use(express.static('public'));
    this.app.get('/', this.sendIndex);
    this.setupOperations();
  }

  sendIndex(req, res) {
    res.sendFile(path.join(__dirname, 'public', 'index.html'));
  }

  setupOperations() {
    this.operations = {
      '/api/foaas/joke': this.handleJoke.bind(this),
      '/api/foaas/whatever': this.handleWhatever.bind(this)
    };
    this.operationsArray = Object.keys(this.operations).map(key => ({
      name: key.split('/').pop(),
      description: 'A funny response'
    }));
  }

  handleJoke(req, res) {
    const message = "You're a joke!";
    const subtitle = "Your name is here";
    this.output(req, res, message, subtitle);
  }

  handleWhatever(req, res) {
    const message = "You're not funny!";
    const subtitle = "Your name is here";
    this.output(req, res, message, subtitle);
  }

  output(req, res, message, subtitle) {
    res.status(200).json({
      status: 'success',
      message,
      subtitle
    });
  }

  start(port) {
    if (!port) throw new Error('Port is required');
    this.app.listen(port, () => {
      console.log(`Server started on port ${port}`);
    });
  }
}

