function startServer() {
  const express = require('express');
  const connectRest = require('connect-rest');
  const bodyParser = require('body-parser');
  const http = require('http');
  const mongoose = require('mongoose');
  const cluster = require('cluster');
  const domain = require('domain');

  const app = express();
  const rest = connectRest();
  app.use(bodyParser.json());
  app.use(rest);

  rest.get('/attractions', async (req, res) => {
    try {
      const attractions = await Attraction.find({ approved: true });
      res.json(attractions);
    } catch (err) {
      res.status(500).json({ error: 'Internal server error' });
    }
  });

  rest.post('/attraction', async (req, res) => {
    try {
      const attraction = new Attraction(req.body);
      await attraction.save();
      res.status(201).json(attraction);
    } catch (err) {
      res.status(500).json({ error: 'Error creating attraction' });
    }
  });

  rest.get('/attraction/:id', async (req, res) => {
    try {
      const attraction = await Attraction.findById(req.params.id);
      if (!attraction) {
        res.status(404).json({ error: 'Attraction not found' });
      } else {
        res.json(attraction);
      }
    } catch (err) {
      res.status(500).json({ error: 'Error fetching attraction' });
    }
  });

  const serverDomain = domain.create();
  serverDomain.on('error', (err) => {
    console.error('Server error:', err);
    serverDomain.destroy();
    process.exit(1);
  });

  const server = http.createServer(app);
  serverDomain.bind(server);

  mongoose.connect(process.env.MONGO_URI, { useNewUrlParser: true, useUnifiedTopology: true })
    .then(() => console.log('Connected to MongoDB'))
    .catch((err) => {
      serverDomain.emit('error', err);
    });

  if (cluster.isMaster) {
    console.log(`Master process ${process.pid} is running`);
    for (let i = 0; i < 4; i++) {
      cluster.fork();
    }

    cluster.on('exit', (worker, code, signal) => {
      console.log(`Worker ${worker.process.pid} died with code ${code} and signal ${signal}`);
      cluster.fork();
    });
  } else {
    server.on('error', (err) => {
      serverDomain.emit('error', err);
    });

    server.listen(process.env.PORT || 3000, () => {
      console.log(`Server listening on port ${process.env.PORT || 3000}`);
    });
  }

  return server;
}

