function setupServer() {
  const next = require('next');
  const express = require('express');
  const session = require('express-session');
  const MongoStore = require('connect-mongo')(session);
  const mongoose = require('mongoose');

  const dev = process.env.NODE_ENV !== 'production';
  const app = next({ dev });
  const handle = app.getRequestHandler();

  mongoose.connect(process.env.MONGO_URL_TEST, { useNewUrlParser: true, useUnifiedTopology: true });

  app.prepare().then(() => {
    const server = express();

    server.use(session({
      secret: process.env.SESSION_SECRET,
      resave: false,
      saveUninitialized: true,
      store: new MongoStore({
        mongoUrl: process.env.MONGO_URL_TEST,
        ttl: 24 * 60 * 60
      })
    }));

    server.get('/api/test', (req, res) => {
      res.json({ message: 'Test route' });
    });

    server.get('*', (req, res) => {
      return handle(req, res);
    });

    server.listen(process.env.PORT || 8000, (err) => {
      if (err) throw err;
      console.log(`Server ready on port ${process.env.PORT || 8000}`);
    });
  });
}
