const setupExpressServer = () => {
  const express = require('express');
  const app = express();
  const fs = require('fs');
  const nodemailer = require('nodemailer');
  const lcrypto = require('lcrypto');
  const lutil = require('lutil');
  const auth = require('./auth');
  const processInfo = require('./processInfo');
  const locker = require('./locker');
  const request = require('request');

  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));
  app.use(express.cookieParser());

  app.post('/auth', (req, res) => {
    const { username, password } = req.body;
    if (auth.authenticate(username, password)) {
      res.status(200).send('Authentication successful');
    } else {
      res.status(401).send('Invalid credentials');
    }
  });

  app.post('/send-test-email', (req, res) => {
    const { to, subject, text } = req.body;
    const transporter = nodemailer.createTransport({
      service: 'gmail',
      auth: {
        user: auth.email,
        pass: lcrypto.decrypt(auth.emailPassword)
      }
    });
    transporter.sendMail({
      to,
      subject,
      text
    }, (error, info) => {
      if (error) {
        res.status(500).send('Error sending email');
      } else {
        res.status(200).send('Email sent');
      }
    });
  });

  app.get('/email-config', (req, res) => {
    const configPath = lutil.join(processInfo.configDir, 'email-config.json');
    fs.readFile(configPath, 'utf8', (err, data) => {
      if (err) {
        res.status(500).send('Error reading configuration');
      } else {
        res.status(200).json(JSON.parse(data));
      }
    });
  });

  app.post('/save-email-config', (req, res) => {
    const { smtpHost, smtpPort, smtpUser, smtpPass, smtpSecure } = req.body;
    const config = {
      smtpHost,
      smtpPort,
      smtpUser,
      smtpPass: lcrypto.encrypt(smtpPass),
      smtpSecure
    };
    const configPath = lutil.join(processInfo.configDir, 'email-config.json');
    fs.writeFile(configPath, JSON.stringify(config), (err) => {
      if (err) {
        res.status(500).send('Error saving configuration');
      } else {
        res.status(200).send('Configuration saved');
      }
    });
  });

  app.listen(processInfo.port, () => {
    console.log(`Email server running on port ${processInfo.port}`);
  });
};

