const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = config.path;
  }

  updateServer() {
    const serverOptions = {
      port: this.config.port,
      host: this.config.host || '0.0.0.0'
    };

    this._http = http.createServer((req, res) => {
      this.sockets.push(res);
      const filePath = path.join(this.path, req.url === '/' ? '/index.html' : req.url);

      send(req, filePath)
        .on('error', (err) => {
          if (err.status === 404) {
            this.handle404(req, res);
          } else {
            this.handleError(err, req, res);
          }
        })
        .on('directory', () => {
          this.handleDirectory(req, res);
        })
        .pipe(res);
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
    });

    this._http.on('close', (socket) => {
      const index = this.sockets.indexOf(socket);
      if (index > -1) {
        this.sockets.splice(index, 1);
      }
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port, this.config.host, () => {
      console.log(`Server running at http://${this.config.host}:${this.config.port}/`);
      if (callback) callback();
    });
  }

  stop(callback) {
    if (this._http) {
      this._http.close(() => {
        this.sockets.forEach(socket => socket.destroy());
        this.sockets = [];
        if (callback) callback();
      });
    }
  }

  handle404(req, res) {
    const html = `
      <html>
        <head><title>404 Not Found</title></head>
        <body>
          <h1>404 Not Found</h1>
          <p>The requested file was not found.</p>
        </body>
      </html>
    `;
    res.writeHead(404, { 'Content-Type': 'text/html' });
    res.end(html);
  }

  handleError(err, req, res) {
    console.error('Error handling request:', err);
    res.writeHead(500, { 'Content-Type': 'text/plain' });
    res.end('Internal Server Error');
  }

  handleDirectory(req, res) {
    const dirPath = path.join(this.path, req.url);
    const files = fs.readdirSync(dirPath);
    let html = `
      <html>
        <head><title>Directory Listing</title></head>
        <body>
          <h1>Directory Listing</h1>
          <ul>
    `;

    files.forEach(file => {
      const fullPath = path.join(dirPath, file);
      const isDir = fs.lstatSync(fullPath).isDirectory();
      const link = isDir ? `${req.url}/${file}/` : `${req.url}/${file}`;
      html += `<li><a href="${link}">${file}${isDir ? '/' : ''}</a></li>`;
    });

    html += `
          </ul>
        </body>
      </html>
    `;
    res.writeHead(200, { 'Content-Type': 'text/html' });
    res.end(html);
  }
}

