  if (packet.length < 32) return;
  const codeBuffer = packet.slice(0, 16);
  const timestampBuffer = packet.slice(16, 24);
  const hashBuffer = packet.slice(24, 32);
  const dataBuffer = packet.slice(32);

  const timestamp = parseInt(timestampBuffer.toString('hex'), 16);
