class MockServer {
  constructor(port, host, options) {
    this.port = port;
    this.host = host;
    this.family = options.family || 'ipv4';
    this.tlsEnabled = options.tlsEnabled || false;
    this.state = 'stopped';
    this.connections = 0;
    this.sockets = [];
    this.server = null;
  }

  async start() {
    if (this.state !== 'stopped') {
      throw new Error('Server is already running');
    }

    this.state = 'running';

    const serverOptions = {
      host: this.host,
      port: this.port,
      family: this.family
    };

    if (this.tlsEnabled) {
      const tlsOptions = {
        key: options.key,
        cert: options.cert,
        ca: options.ca
      };
      this.server = require('tls').createServer(tlsOptions, (socket) => {
        this.handleConnection(socket);
      });
    } else {
      this.server = require('net').createServer((socket) => {
        this.handleConnection(socket);
      });
    }

    return new Promise((resolve, reject) => {
      this.server.on('error', (err) => {
        this.state = 'stopped';
        reject(err);
      });

      this.server.on('listening', () => {
        this.state = 'running';
        resolve();
      });

      this.server.listen(serverOptions);
    });
  }

  handleConnection(socket) {
    this.sockets.push(socket);
    this.connections++;
    socket.on('data', (data) => {
      console.log(`Received data: ${data}`);
    });
    socket.on('end', () => {
      this.sockets = this.sockets.filter(s => s !== socket);
      this.connections--;
    });
    socket.on('error', (err) => {
      console.error(`Socket error: ${err.message}`);
    });
  }

  async destroy() {
    if (this.state !== 'running') {
      throw new Error('Server is not running');
    }

    this.state = 'destroyed';

    return new Promise((resolve) => {
      this.server.close(() => {
        this.sockets.forEach(socket => socket.destroy());
        this.sockets = [];
        this.connections = 0;
        resolve();
      });
    });
  }
}

