    // Proxy protocol handling logic
    if (socket.readable && socket.readable.length > 0) {
      const proxyHeader = socket.read(24);
      if (proxyHeader.startsWith('PROXY TCP4')) {
        const ip = proxyHeader.slice(10, 22).trim();
        const port = parseInt(proxyHeader.slice(22, 30), 10);
        console.log(`Proxy connection from ${ip}:${port}`);
