      this.send(peer, data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
    this.emit('peer_connected', remote_id);
  }

  remove_peer(socket) {
    for (const [id, peer] of Object.entries(this.peers_)) {
      if (peer === socket) {
