const dgram = require('dgram');
const os = require('os');

class ServerDiscovery {
  constructor() {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = Date.now();
    this.broadcastIP = this.calcBroadcastIP();
    this.discoveryListener = null;
  }

  setupDiscovery(callback) {
    this.discoveryListener = dgram.createSocket('udp4');
    this.discoveryListener.on('listening', () => {
      if (callback) callback();
    });
    this.discoveryListener.bind(5353);
  }

  discoveryBroadcast(type, message, callback) {
    const client = dgram.createSocket('udp4');
    const data = JSON.stringify({ type, message });
    client.send(data, 0, data.length, 5353, this.broadcastIP, (err) => {
      client.close();
      if (callback) callback(err);
    });
  }

  discoveryReceive(msg, rinfo) {
    try {
      const jsonMsg = JSON.parse(msg.toString());
      if (jsonMsg.type === 'heartbeat') {
        const { hostname, port } = jsonMsg.message;
        this.nearbyServers[hostname] = { hostname, port };
      }
    } catch (e) {
      console.error('Error parsing discovery message:', e);
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
    }
  }

  calcBroadcastIP() {
    const interfaces = os.networkInterfaces();
    for (const interfaceName in interfaces) {
      const interfaceList = interfaces[interfaceName];
      for (const iface of interfaceList) {
        if (!iface.internal && iface.family === 'IPv4') {
          const ip = iface.address;
          const netmask = iface.netmask;
          const broadcast = this.calculateBroadcastAddress(ip, netmask);
          return broadcast;
        }
      }
    }
    return '255.255.255.255';
  }

  calculateBroadcastAddress(ip, netmask) {
    const ipParts = ip.split('.').map(Number);
    const maskParts = netmask.split('.').map(Number);
    let broadcast = [];
    for (let i = 0; i < 4; i++) {
      broadcast.push(ipParts[i] | maskParts[i]);
    }
    return broadcast.join('.');
  }
}

