class MasterIPC {  
  constructor(options) {  
    this.options = options || {};  
    this._sock = null;  
    this.children = [];  
    this.sock = null;  
  }  

  IPCSocket() {  
    if (!this._sock) {  
      this._sock = require('dgram').createSocket('udp4');  
      this._sock.on('message', (msg, rinfo) => {  
        const data = JSON.parse(msg.toString());  
        if (data.type === 'response') {  
          this.emit('response', data, rinfo);  
        } else if (data.type === 'event') {  
          this.emit('event', data, rinfo);  
        }  
      });  
    }  
    return this._sock;  
  }  

  setupIPC() {  
    const { port = 5000, address = '0.0.0.0' } = this.options;  
    this.sock = this.IPCSocket();  
    this.sock.bind(port, address, () => {  
      this.emit('start');  
    });  
  }  

  call(id, method, ...args) {  
    const message = {  
      type: 'request',  
      id,  
      method,  
      args  
    };  
    this.sock.send(JSON.stringify(message), 0, message.length, this.options.port, this.options.address, (err) => {  
      if (err) console.error(err);  
    });  
  }  

  invoke(method, args, worker) {  
    if (worker && typeof worker[method] === 'function') {  
      worker[method].apply(worker, args);  
    }  
  }  

  connect(worker) {  
    this.children.push(worker);  
    this.emit('worker connected', worker);  
  }  

  connectMaster(sig) {  
    if (this.sock.listening) {  
      setTimeout(() => {  
        process.kill(process.pid, sig);  
      }, 1000);  
    } else {  
      process.kill(process.pid, sig);  
    }  
  }  

  masterKilled(data) {  
    this.emit('restart', data);  
  }  

  emit(event, data, rinfo) {  
    if (this.children.some(child => child.id === data.id)) {  
      this.children.find(child => child.id === data.id).emit(event, data, rinfo);  
    }  
  }  
}

