function manageParallelTests() {
    const socket = new WebSocket('ws://testserver.com');
    let maxParallelRuns = 5;
    let subscriptionKey = Date.now();
    let queue = [];
    let numRunning = 0;

    function processQueue() {
        while (numRunning < maxParallelRuns && queue.length > 0) {
            const test = queue.shift();
            openTestWindow(test);
            numRunning++;
        }
    }

    socket.onopen = function() {
        socket.send(JSON.stringify({
            type: 'subscribe',
            key: subscriptionKey
        }));
    };

    socket.onmessage = function(event) {
        const data = JSON.parse(event.data);
        if (data.type === 'test_result') {
            updateTestStatus(data.testId, data.status, data.result);
            numRunning--;
            processQueue();
        } else if (data.type === 'test_status') {
            updateTestStatus(data.testId, data.status, '');
        } else if (data.type === 'test_error') {
            updateTestStatus(data.testId, 'error', data.message);
            numRunning--;
            processQueue();
        }
    };

    function openTestWindow(test) {
        const win = window.open(test.url, '_blank');
        win.onload = function() {
            win.postMessage({
                type: 'start_test',
                subscriptionKey: subscriptionKey,
                testId: test.id
            }, '*');
        };
    }

    function updateTestStatus(testId, status, result) {
        const statusDiv = document.getElementById('status-' + testId);
        if (statusDiv) {
            statusDiv.textContent = status + (result ? ': ' + result : '');
        }
    }

    return {
        addTest: function(test) {
            queue.push(test);
            processQueue();
        }
    };
}

