class LiveQueryClient {
  constructor({ applicationId, serverURL, javascriptKey, masterKey, sessionToken, installationId }) {
    this.attempts = 0;
    this.id = null;
    this.requestId = 0;
    this.applicationId = applicationId;
    this.serverURL = serverURL;
    this.javascriptKey = javascriptKey;
    this.masterKey = masterKey;
    this.sessionToken = sessionToken;
    this.installationId = installationId;
    this.additionalProperties = true;
    this.connectPromise = new Promise((resolve, reject) => {
      this.connectPromiseResolve = resolve;
      this.connectPromiseReject = reject;
    });
    this.subscriptions = new Map();
    this.socket = null;
    this.state = 'INITIALIZED';
    this.reconnectHandle = null;
    this.emitter = new EventEmitter();
    this.on = this.emitter.on;
    this.emit = this.emitter.emit;
  }

  open() {
    if (this.state !== 'INITIALIZED') {
      return;
    }
    this.state = 'CONNECTING';
    this.socket = new WebSocketController(this.serverURL);
    this.socket.onopen = () => this._handleWebSocketOpen();
    this.socket.onmessage = (event) => this._handleWebSocketMessage(event);
    this.socket.onclose = () => this._handleWebSocketClose();
    this.socket.onerror = (error) => this._handleWebSocketError(error);
  }

  _handleWebSocketOpen() {
    this.state = 'CONNECTED';
    this.emit('open');
    this._sendConnectionRequest();
  }

  _sendConnectionRequest() {
    const headers = {};
    if (this.javascriptKey) {
      headers['X-Parse-JavaScript-Key'] = this.javascriptKey;
    }
    if (this.masterKey) {
      headers['X-Parse-Master-Key'] = this.masterKey;
    }
    if (this.sessionToken) {
      headers['X-Parse-Session-Token'] = this.sessionToken;
    }
    if (this.installationId) {
      headers['X-Parse-Installation-ID'] = this.installationId;
    }
    if (this.additionalProperties) {
      headers['X-Parse-Additional-Properties'] = 'true';
    }
    this.socket.send(JSON.stringify({
      type: 'connection_request',
      applicationId: this.applicationId,
      headers: headers
    }));
  }

  _handleWebSocketMessage(event) {
    const data = JSON.parse(event.data);
    if (data.type === 'connection_success') {
      this.id = data.id;
      this.emit('connected');
      this.connectPromiseResolve();
    } else if (data.type === 'subscription_response') {
      const subscription = new LiveQuerySubscription(this, data.subscriptionId, data.query);
      this.subscriptions.set(data.subscriptionId, subscription);
      this.emit('subscription', subscription);
    } else if (data.type === 'event') {
      this.emit('event', data);
    } else if (data.type === 'error') {
      this.emit('error', data.message);
      this._handleWebSocketClose();
    }
  }

  _handleWebSocketClose() {
    this.state = 'DISCONNECTED';
    this.emit('close');
    this._handleReconnect();
  }

  _handleWebSocketError(error) {
    this.emit('error', error.message);
    this._handleReconnect();
  }

  _handleReconnect() {
    clearTimeout(this.reconnectHandle);
    this.attempts++;
    const delay = Math.min(1000 * Math.pow(2, this.attempts), 30000);
    this.reconnectHandle = setTimeout(() => {
      this.open();
    }, delay);
  }
}

class WebSocketController {
  constructor(url) {
    this.url = url;
    this.socket = new WebSocket(url);
    this.socket.onopen = () => this.onopen();
    this.socket.onmessage = (event) => this.onmessage(event);
    this.socket.onclose = () => this.onclose();
    this.socket.onerror = (error) => this.onerror(error);
  }

  onopen() {
    this.emit('open');
  }

  onmessage(event) {
    this.emit('message', event);
  }

  onclose() {
    this.emit('close');
  }

  onerror(error) {
    this.emit('error', error);
  }

  send(data) {
    this.socket.send(data);
  }

  emit(event, ...args) {
    if (this[event]) {
      this[event](...args);
    }
  }
}

class LiveQuerySubscription {
  constructor(client, id, query) {
    this.client = client;
    this.id = id;
    this.query = query;
    this.state = 'PENDING';
    this.client.emit('subscription', this);
  }

  subscribe() {
    if (this.state === 'PENDING') {
      this.state = 'SUBSCRIBED';
      this.client.socket.send(JSON.stringify({
        type: 'subscribe',
        subscriptionId: this.id,
        query: this.query
      }));
    }
  }

  unsubscribe() {
    if (this.state === 'SUBSCRIBED') {
      this.state = 'UNSUBSCRIBED';
      this.client.socket.send(JSON.stringify({
        type: 'unsubscribe',
        subscriptionId: this.id
      }));
      this.client.subscriptions.delete(this.id);
    }
  }
}

class EventEmitter {
  constructor() {
    this.listeners = {};
  }

  on(event, listener) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event].push(listener);
  }

  emit(event, ...args) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(listener => listener(...args));
    }
  }
}

