startRecording = function(data) {
  const { host, port, language, task, modelSize, useVad } = data;
  const wsUrl = `ws://${host}:${port}`;
  const ws = new WebSocket(wsUrl);

  let isServerReady = false;
  let isCapturing = false;
  let audioContext;
  let mediaStream;
  let audioSource;
  let analyser;
  let dataArray;
  let bufferLength;
  let isVADActive = useVad;
  let lastVADCheckTime = 0;
  let vadThreshold = 0.5;
  let isSilenceDetected = true;

  navigator.mediaDevices.getUserMedia({ audio: true })
    .then(stream => {
      mediaStream = stream;
      audioContext = new (window.AudioContext || window.webkitAudioContext)();
      audioSource = audioContext.createMediaStreamSource(stream);
      analyser = audioContext.createAnalyser();
      analyser.fftSize = 2048;
      bufferLength = analyser.frequencyBinCount;
      dataArray = new Uint8Array(bufferLength);
      audioSource.connect(analyser);
      startAudioProcessing();
    })
    .catch(err => {
      console.error('Microphone access denied:', err);
      browser.runtime.sendMessage({ type: 'microphone_error', message: 'Microphone access denied' });
    });

  ws.onopen = () => {
    console.log('WebSocket connected');
    browser.runtime.sendMessage({ type: 'websocket_connected' });
    isServerReady = true;
    sendInitialConfig();
  };

  ws.onmessage = event => {
    const response = JSON.parse(event.data);
    if (response.type === 'transcription') {
      browser.runtime.sendMessage({ type: 'transcription_result', text: response.text });
    } else if (response.type === 'status') {
      browser.runtime.sendMessage({ type: 'server_status', status: response.status });
    }
  };

  ws.onerror = error => {
    console.error('WebSocket error:', error);
    browser.runtime.sendMessage({ type: 'websocket_error', message: error.message });
  };

  ws.onclose = () => {
    console.log('WebSocket disconnected');
    browser.runtime.sendMessage({ type: 'websocket_disconnected' });
    isServerReady = false;
    stopAudioProcessing();
  };

  function sendInitialConfig() {
    const config = {
      task,
      language,
      modelSize,
      useVad
    };
    ws.send(JSON.stringify(config));
  }

  function startAudioProcessing() {
    isCapturing = true;
    requestAnimationFrame(processAudio);
  }

  function stopAudioProcessing() {
    isCapturing = false;
    if (audioContext && audioContext.state !== 'closed') {
      audioContext.close();
    }
    if (mediaStream) {
      mediaStream.getTracks().forEach(track => track.stop());
    }
  }

  function processAudio() {
    if (!isCapturing || !isServerReady) return;
    analyser.getByteFrequencyData(dataArray);
    let rms = calculateRMS(dataArray);
    let currentTime = performance.now();

    if (isVADActive) {
      if (currentTime - lastVADCheckTime > 100) {
        lastVADCheckTime = currentTime;
        if (rms > vadThreshold) {
          if (isSilenceDetected) {
            browser.runtime.sendMessage({ type: 'vad_active', active: true });
            isSilenceDetected = false;
          }
        } else {
          if (!isSilenceDetected) {
            browser.runtime.sendMessage({ type: 'vad_active', active: false });
            isSilenceDetected = true;
          }
        }
      }
    }

    ws.send(JSON.stringify({ type: 'audio_data', data: dataArray }));
    requestAnimationFrame(processAudio);
  }

  function calculateRMS(array) {
    let sum = 0;
    for (let i = 0; i < array.length; i++) {
      sum += array[i] * array[i];
    }
    return Math.sqrt(sum / array.length);
  }
};

