    const socket = new WebSocket('wss://chat-service.com');
    const socketId = Math.random().toString(36).substring(2, 9);
    this.webSockets[socketId] = socket;
    socket.onopen = () => {
      socket.send(JSON.stringify({ type: 'handshake' }));
      resolve(socketId);
    };
    socket.onerror = (error) => {
      reject(new Error('WebSocket connection error: ' + error.message));
    };
    socket.onclose = () => {
      delete this.webSockets[socketId];
      reject(new Error('WebSocket connection closed'));
    };
  });
}

async function ping(socketId) {
  if (!this.webSockets[socketId] || this.webSockets[socketId].readyState !== WebSocket.OPEN) {
    throw new Error('WebSocket not connected for socketId: ' + socketId);
  }
  this.webSockets[socketId].send(JSON.stringify({ type: 'ping' }));
}

async function send(socketId, msg, onMessage) {
  if (!this.webSockets[socketId] || this.webSockets[socketId].readyState !== WebSocket.OPEN) {
    throw new Error('WebSocket not connected for socketId: ' + socketId);
  }
  const socket = this.webSockets[socketId];
  socket.send(JSON.stringify(msg));
  return new Promise((resolve) => {
    const messageHandler = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 2) {
        socket.removeEventListener('message', messageHandler);
        resolve(data);
      } else {
        onMessage(data);
      }
    };
    socket.addEventListener('message', messageHandler);
  });
}

async function close(socketId) {
  if (!this.webSockets[socketId]) {
    throw new Error('No WebSocket found for socketId: ' + socketId);
  }
  const socket = this.webSockets[socketId];
  socket.close();
  delete this.webSockets[socketId];
}

