async function fetchSubscribedTVChannels(lang = 'id') {
  const puppeteer = require('puppeteer');
  const axios = require('axios');

  const browser = await puppeteer.launch({ headless: true });
  const page = await browser.newPage();

  try {
    // Step 1: Scrape initial configuration data from a webpage
    await page.goto('https://example-streaming-service.com/config');
    const configData = await page.evaluate(() => {
      const script = document.querySelector('script[id="config-script"]');
      return script ? script.textContent : null;
    });

    if (!configData) {
      throw new Error('Configuration data not found on the webpage.');
    }

    const config = JSON.parse(configData);
    const { authUrl, apiKey, channelPackagesUrl } = config;

    // Step 2: Authenticate with the service using extracted credentials
    const authResponse = await axios.post(authUrl, {
      apiKey,
      lang
    });

    if (authResponse.status !== 200) {
      throw new Error(`Authentication failed with status code ${authResponse.status}`);
    }

    const { token } = authResponse.data;

    // Step 3: Fetch subscribed channel packages
    const packageResponse = await axios.get(channelPackagesUrl, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

    if (packageResponse.status !== 200) {
      throw new Error(`Failed to fetch channel packages with status code ${packageResponse.status}`);
    }

    const packages = packageResponse.data;

    // Step 4: Process and deduplicate channel information
    const channels = [];
    const seenSiteIds = new Set();

    for (const package of packages) {
      for (const channel of package.channels) {
        if (seenSiteIds.has(channel.site_id)) continue;
        seenSiteIds.add(channel.site_id);

        const channelName = await getChannelName(channel.site_id, lang);
        channels.push({
          lang,
          site_id: channel.site_id,
          name: channelName
        });
      }
    }

    await browser.close();
    return channels;
  } catch (error) {
    await browser.close();
    throw error;
  }
}

async function getChannelName(siteId, lang) {
  const response = await axios.get(`https://example-streaming-service.com/channel/${siteId}`, {
    params: { lang }
  });

  if (response.status !== 200) {
    throw new Error(`Failed to fetch channel name for site ID ${siteId}`);
  }

  return response.data.name;
}

