const { Inspector } = require('inspector');

function createWebSocketServer(port) {
  const wss = new WebSocket.Server({ port });

  wss.on('listening', () => {
    console.log(`WebSocket server is listening on port ${port}`);
  });

  wss.on('error', (err) => {
    console.error('WebSocket server error:', err);
  });

  wss.on('close', () => {
    console.log('WebSocket server has closed');
  });

  wss.on('connection', (ws) => {
    const inspector = new Inspector();
    let session;

    try {
      session = inspector.openSession();
    } catch (e) {
      console.error('Failed to open inspector session:', e);
      ws.close(1011, 'Internal Server Error');
      return;
    }

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());
        session.postMessage(data);
      } catch (e) {
        console.error('Error parsing message:', e);
        ws.send(JSON.stringify({ error: 'Invalid message format' }));
      }
    });

    ws.on('error', (err) => {
      console.error('WebSocket error:', err);
      if (session) {
        session.close();
      }
      ws.close(1011, 'Internal Server Error');
    });

    ws.on('close', () => {
      console.log('Client disconnected');
      if (session) {
        session.close();
      }
    });

    ws.on('unexpectedClose', () => {
      console.log('Client unexpectedly closed the connection');
      if (session) {
        session.close();
      }
    });
  });
}

