const WebSocket = require('ws');

class XVIZServer {
  constructor(handlers, options, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('Handlers array is required');
    }

    this.handlers = handlers;
    this.options = Object.assign({
      port: 8080,
      host: 'localhost',
      logger: console
    }, options);

    this._server = new WebSocket.Server({ port: this.options.port, host: this.options.host });

    this._server.on('connection', (socket, request) => {
      this.handleSession(socket, request).catch(err => {
        this.log('Error handling session:', err);
      });
    });

    if (callback) {
      this._server.on('listening', () => {
        callback(null, this);
      });
    }
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    for (const handler of this.handlers) {
      try {
        await handler.createSession(socket, request);
        return;
      } catch (err) {
        this.log('Handler rejected session:', err);
      }
    }
    socket.close();
  }

  log(...msg) {
    if (this.options.logger && typeof this.options.logger.log === 'function') {
      this.options.logger.log(...msg);
    }
  }
}

