const WebSocket = require('ws');

class AphlictClientServer {
  constructor(server) {
    this._server = server;
    this._lists = {};
    this._adminServers = [];
    this._server.on('upgrade', (request, socket, head) => {
      this._onrequest(request, socket, head);
    });
  }

  _onrequest(request, socket, head) {
    const path = request.url;
    if (path.startsWith('/websocket')) {
      const instance = this._parseInstanceFromPath(path);
      const wss = new WebSocket.Server({ server: this._server, path: '/websocket' });
      wss.on('connection', (ws) => {
        this.log(`Client connected to instance ${instance}`);
        ws.on('message', (message) => {
          this._processMessage(ws, message, instance);
        });
        ws.on('close', () => {
          this.log(`Client disconnected from instance ${instance}`);
        });
      });
    } else {
      socket.destroy();
    }
  }

  _parseInstanceFromPath(path) {
    const parts = path.split('/');
    return parts[2] || 'default';
  }

  listen() {
    this._server.listen(8080, () => {
      this.log('WebSocket server is running on port 8080');
    });
  }

  getListenerList(instance) {
    if (!this._lists[instance]) {
      this._lists[instance] = [];
    }
    return this._lists[instance];
  }

  log(message) {
    console.log(`[AphlictClientServer] ${message}`);
  }

  trace(message) {
    console.trace(`[AphlictClientServer] ${message}`);
  }

  getHistory(min_age) {
    // This would typically query a database or storage for historical messages
    return [];
  }

  _processMessage(ws, message, instance) {
    const data = JSON.parse(message.toString());
    switch (data.type) {
      case 'subscribe':
        this.getListenerList(instance).push(ws);
        this.log(`Client subscribed to instance ${instance}`);
        break;
      case 'unsubscribe':
        const listeners = this.getListenerList(instance);
        const index = listeners.indexOf(ws);
        if (index !== -1) {
          listeners.splice(index, 1);
          this.log(`Client unsubscribed from instance ${instance}`);
        }
        break;
      case 'replay':
        const history = this.getHistory(data.min_age);
        ws.send(JSON.stringify({ type: 'replay', messages: history }));
        break;
      case 'ping':
        ws.send(JSON.stringify({ type: 'pong' }));
        break;
      default:
        this.trace(`Unknown message type: ${data.type}`);
    }
  }
}

