    let page = 1;  
    let hasMore = true;  
    while (hasMore) {  
      try {  
        const response = await axios.get(`https://api.example.com/channels?region=${region}&page=${page}`);  
        allChannels.push(...response.data.channels);  
        hasMore = response.data.hasNext;  
        page++;  
      } catch (error) {  
        console.error(`Error fetching data for region ${region}:`, error.message);  
        hasMore = false;  
      }  
    }  
  }  

  return uniqBy(allChannels, 'site_id');  
};

