    this._xhr.onloadend = this._onLoadEnd.bind(this);
    this._xhr.onerror = this._onXhrError.bind(this);
    this._xhr.onreadystatechange = this._onReadyStateChange.bind(this);

    if (this._range) {
      const start = this._range.start || 0;
      const end = this._range.end || null;
      this._xhr.setRequestHeader('Range', `bytes=${start}-${end}`);
    }

    this._xhr.send();
  }

  abort() {
    if (this._xhr) {
      this._requestAbort = true;
      this._xhr.abort();
    }
  }

