class EventSource {
  constructor(URL) {
    this.URL = URL;
    this.readyState = 0; // CONNECTING
    this._pollTimer = null;
    this._xhr = null;
    this._eventHandlers = {};
  }

  poll() {
    if (this.readyState !== 1) return;
    this._xhr = new XMLHttpRequest();
    this._xhr.open('GET', this.URL, true);
    this._xhr.setRequestHeader('Accept', 'text/event-stream');
    this._xhr.onreadystatechange = () => {
      if (this._xhr.readyState === 4) {
        if (this._xhr.status === 200) {
          this._processEvents();
        } else {
          this._handleError();
        }
      }
    };
    this._xhr.send();
  }

  close() {
    if (this._xhr) {
      this._xhr.abort();
      this._xhr = null;
    }
    this.readyState = 2; // CLOSED
    if (this._pollTimer) {
      clearInterval(this._pollTimer);
      this._pollTimer = null;
    }
  }

  dispatchEvent(type, event) {
    if (this._eventHandlers[type]) {
      this._eventHandlers[type].forEach(handler => handler(event));
    }
  }

  addEventListener(type, handler) {
    if (!this._eventHandlers[type]) {
      this._eventHandlers[type] = [];
    }
    this._eventHandlers[type].push(handler);
  }

  removeEventListener() {
    // Placeholder method for removing event listeners
  }

  _processEvents() {
    const reader = new FileReader();
    reader.onload = () => {
      const lines = reader.result.split('\n');
      lines.forEach(line => {
        if (line.trim() === '') return;
        const [key, value] = line.split(': ');
        if (key === 'data') {
          this.dispatchEvent('message', new MessageEvent(value, this.URL, this._lastEventId));
        } else if (key === 'id') {
          this._lastEventId = value;
        }
      });
      this.poll();
    };
    reader.readAsText(this._xhr.responseText);
  }

  _handleError() {
    this.readyState = 2; // CLOSED
    if (this._pollTimer) {
      clearInterval(this._pollTimer);
      this._pollTimer = null;
    }
    this._pollTimer = setInterval(() => {
      if (this.readyState === 2) {
