    const tempFilePath = path.join(__dirname, 'temp', 'input.txt');
    const pdfFilePath = path.join(__dirname, 'output', 'output.pdf');

    fs.writeFileSync(tempFilePath, content);
    exec(`python generate_pdf.py ${tempFilePath} ${pdfFilePath}`, (error, stdout, stderr) => {
      if (error) {
        console.error(`Error executing command: ${error.message}`);
        return res.status(500).send('Error generating PDF');
      }
      if (stderr) {
        console.error(`Command stderr: ${stderr}`);
        return res.status(500).send('Error generating PDF');
      }
      res.download(pdfFilePath, 'output.pdf', (err) => {
        if (err) {
          console.error(`Download error: ${err.message}`);
          return res.status(500).send('Error downloading PDF');
        }
        fs.unlinkSync(tempFilePath);
        fs.unlinkSync(pdfFilePath);
      });
    });
  });

