const TwitterHTTPClient = class {
  constructor(config) {
    this.config = config;
    this._twitter_time_minus_local_time_ms = 0;
  }

  request(method, path, params, callback) {
    return new Promise((resolve, reject) => {
      const twitOptions = {
        method,
        path,
        params
      };
      this._doRestApiRequest(twitOptions, twitOptions, method, (err, data) => {
        if (err) {
          reject(err);
        } else {
          resolve(data);
        }
      });
    });
  }

  _doRestApiRequest(reqOpts, twitOptions, method, callback) {
    const url = `https://api.twitter.com${reqOpts.path}`;
    const headers = {
      'Authorization': this._getAuthorizationHeader(twitOptions),
      'Content-Type': 'application/json'
    };

    const options = {
      method,
      url,
      headers,
      json: true
    };

    if (reqOpts.params) {
      options.qs = reqOpts.params;
    }

    require('request')(options, (error, response, body) => {
      if (error) {
        callback(error, null);
        return;
      }

      if (response.statusCode >= 400) {
        callback(new Error(`Twitter API error: ${response.statusMessage}`), null);
        return;
      }

      this._updateClockOffsetFromResponse(response);

      callback(null, body);
    });
  }

  _getAuthorizationHeader(options) {
    const authType = 'OAuth ';
    const authParams = {
      oauth_consumer_key: this.config.consumerKey,
      oauth_signature_method: 'HMAC-SHA1',
      oauth_timestamp: Math.floor(Date.now() / 1000),
      oauth_nonce: Math.random().toString(36).substring(2),
      oauth_version: '1.0'
    };

    const baseString = `${options.method.toUpperCase()}&${encodeURIComponent(options.path)}&`;
    const keys = Object.keys(authParams).sort();
    const signatureBase = keys.map(k => `${k}=${encodeURIComponent(authParams[k])}`).join('&');

    const secret = encodeURIComponent(this.config.consumerSecret) + '&' + encodeURIComponent(this.config.accessTokenSecret);
    const hmac = crypto.createHmac('sha1', secret);
    hmac.update(signatureBase);
    const signature = hmac.digest('base64');

    authParams.oauth_signature = signature;
    const authHeader = authType + keys.map(k => `${k}="${encodeURIComponent(authParams[k])}"`).join(', ');

    return authHeader;
  }

  _updateClockOffsetFromResponse(resp) {
    const serverTime = new Date(resp.headers.date).getTime();
    const localTime = Date.now();
    this._twitter_time_minus_local_time_ms = serverTime - localTime;
  }
};

