    if (!port || typeof port !== 'number') {
      throw new Error('Port must be a valid number');
    }
    this.app.use(express.static(__dirname));
    this.app.get('/', this.sendIndex.bind(this));
    this.app.get('/foaas/:operation', (req, res) => {
