      } else {
        res.status(404).send('Operation not found');
      }
    });
    this.app.listen(port, () => {
      console.log(`FOAAS server running on port ${port}`);
    });
  }

  output(req, res, message, subtitle) {
    res.json({
      message: message,
      subtitle: subtitle
    });
  }

  addOperation(name, message, subtitle) {
    this.operations[name] = { message, subtitle };
    this.operationsArray.push({ name, message, subtitle });
  }
