  if (!name || !description || !location || !email) {
    return res.status(400).json({ error: 'Missing required fields' });
  }
  try {
    const attraction = new Attraction({ name, description, location, email });
    await attraction.save();
    res.status(201).json({ id: attraction._id });
  } catch (err) {
