function fetch_content($params, $template) {
    $file = $params['file'];
    $assign = $params['assign'] ?? null;
    $assign_headers = $params['assign_headers'] ?? null;
    $user = $params['user'] ?? null;
    $pass = $params['pass'] ?? null;
    $proxy_host = $params['proxy_host'] ?? null;
    $proxy_port = $params['proxy_port'] ?? 80;

    if (strpos($file, 'http://') === 0 || strpos($file, 'https://') === 0) {
        $url = parse_url($file);
        $host = $url['host'];
        $path = $url['path'] . (isset($url['query']) ? '?' . $url['query'] : '');
        $port = $url['port'] ?? 80;
        $scheme = $url['scheme'];
        $headers = [
            'Host: ' . $host,
            'User-Agent: PHP',
            'Accept: */*',
        ];

        if ($user && $pass) {
            $headers[] = 'Authorization: Basic ' . base64_encode($user . ':' . $pass);
        }

        if ($proxy_host) {
            $fp = fsockopen($proxy_host, $proxy_port, $errno, $errstr, 30);
        } else {
            $fp = fsockopen($host, $port, $errno, $errstr, 30);
        }

        if (!$fp) {
            return;
        }

        $request = "GET $path HTTP/1.1\r\n";
        $request .= implode("\r\n", $headers) . "\r\n\r\n";
        fwrite($fp, $request);

        $response = '';
        while (!feof($fp)) {
            $response .= fgets($fp, 1024);
        }
        fclose($fp);

        $response = explode("\r\n\r\n", $response, 2);
        $headers = $response[0];
        $body = $response[1];

        if ($assign_headers) {
            $template->assign($assign_headers, $headers);
        }

        if ($assign) {
            $template->assign($assign, $body);
        } else {
            return $body;
        }
    } else {
        if ($assign) {
            $template->assign($assign, file_get_contents($file));
        } else {
            return file_get_contents($file);
        }
    }
}
