class CI_Trackback {
    public $charset = 'UTF-8';
    public $data = array();
    public $convert_ascii = TRUE;
    public $response = '';
    public $error_msg = array();

    public function process($url, $data) {
        $this->response = '';
        $this->error_msg = array();

        $url = parse_url($url);
        if ( ! $url OR ! isset($url['scheme'], $url['host'], $url['path'])) {
            $this->set_error('Invalid URL');
            return FALSE;
        }

        $data = $this->_prep_data($data);

        $fp = @fsockopen($url['host'], 80, $errno, $errstr, 5);
        if ( ! $fp) {
            $this->set_error('Could not connect to host');
            return FALSE;
        }

        $path = $url['path'] . (isset($url['query']) ? '?' . $url['query'] : '');
        $out = "POST $path HTTP/1.1\r\n";
        $out .= "Host: {$url['host']}\r\n";
        $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $out .= "Content-Length: " . strlen($data) . "\r\n";
        $out .= "Connection: Close\r\n\r\n";
        $out .= $data;

        fwrite($fp, $out);

        while ( ! feof($fp)) {
            $this->response .= fgets($fp, 1024);
        }

        fclose($fp);

        return TRUE;
    }

    public function get_id($url) {
        if (preg_match('/trackback\/(\d+)/', $url, $matches)) {
            return $matches[1];
        }

        return FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }

    private function _prep_data($data) {
        if (is_array($data)) {
            $data = array_map('urlencode', $data);
            $data = implode('&', $data);
        }

        if ($this->convert_ascii) {
            $data = utf8_decode($data);
        }

        return $data;
    }
}
