class Http {
    public static function fsockopenDownload($url, $conf = []) {
        $default = [
            'limit' => 0,
            'post' => '',
            'cookie' => '',
            'ip' => '',
            'timeout' => 10,
            'block' => true,
        ];
        $conf += $default;
        $url = parse_url($url);
        $host = $url['host'];
        $path = $url['path'] ? $url['path'] : '/';
        if (isset($url['query'])) {
            $path .= '?' . $url['query'];
        }
        $fp = fsockopen(($conf['ip'] ? $conf['ip'] : $host), 80, $errno, $errstr, $conf['timeout']);
        if (!$fp) {
            return '';
        }
