        if (isset($this->_serialized[$key]) && $this->_serialized[$key] === TRUE) {
            $data = unserialize($data);
        }

        return $data;
    }

    public function save($id, $data, $ttl = 60, $raw = FALSE)
    {
        if ($raw === FALSE) {
            $data = serialize($data);
            $this->_serialized[$id] = TRUE;
        }

        return $this->_redis->setex($this->_key($id), $ttl, $data);
    }

    public function delete($key)
    {
        return $this->_redis->del($this->_key($key));
    }

    public function increment($id, $offset = 1)
    {
        return $this->_redis->incrBy($this->_key($id), $offset);
