<?php

use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\HttpKernel\HttpKernelInterface;
use Symfony\Component\Mime\AbstractPart;
use Symfony\Component\Mime\FormDataPart;
use Symfony\Component\Mime\TextPart;
use Symfony\Component\Mime\MimeTypes;
use Symfony\Component\HttpClient\HttpClient;

class HttpClientKernel implements HttpKernelInterface
{
    private $client;

    public function __construct(?HttpClientInterface $client = null)
    {
        if (null === $client) {
            if (!class_exists(HttpClient::class)) {
                throw new \LogicException('The HttpClient component is not installed. Try running "composer require symfony/http-client".');
            }
            $client = HttpClient::create();
        }
        $this->client = $client;
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response
    {
        $headers = $this->getHeaders($request);
        $body = $this->getBody($request);

        $response = $this->client->request(
            $request->getMethod(),
            $request->getUri(),
            [
                'headers' => $headers,
                'body' => $body,
            ]
        );

        return new Response(
            $response->getContent(),
            $response->getStatusCode(),
            $response->getHeaders()
        );
    }

    private function getBody(Request $request): ?AbstractPart
    {
        if ($request->isMethod('GET') || $request->isMethod('HEAD')) {
            return null;
        }

        if (null === $request->getContent()) {
            return null;
        }

        if (class_exists(MimeTypes::class)) {
            $mimeTypes = new MimeTypes();
            $mimeType = $mimeTypes->guessMimeType($request->headers->get('Content-Type'));
            if ('application/x-www-form-urlencoded' === $mimeType) {
                return new TextPart($request->getContent());
            }
        }

        return new FormDataPart($request->files->all(), $request->request->all());
    }

    private function getHeaders(Request $request): array
    {
        $headers = $request->headers->all();
        $cookies = $request->cookies->all();
        if (!empty($cookies)) {
            $headers['cookie'] = [];
            foreach ($cookies as $name => $value) {
                $headers['cookie'][] = sprintf('%s=%s', $name, $value);
            }
        }

        return $headers;
    }
}
