<?php

use Swoole\Coroutine\Client;
use Swoole\Coroutine\Channel;

class Connection
{
    protected $host;
    protected $port;
    protected $timeout;
    protected $callTimeout;
    protected $middleware = [];
    protected $client;

    public function __construct(array $config = [])
    {
        $this->host = $config['host'] ?? '127.0.0.1';
        $this->port = $config['port'] ?? 8080;
        $this->timeout = $config['timeout'] ?? 5.0;
        $this->callTimeout = $config['callTimeout'] ?? 5.0;
        $this->middleware = $config['middleware'] ?? [];
    }

    public function connect()
    {
        $this->client = new Client(SWOOLE_SOCK_TCP);
        $this->client->set([
            'timeout' => $this->timeout,
            'connect_timeout' => $this->timeout,
        ]);
        $connected = $this->client->connect($this->host, $this->port);
        if (!$connected) {
            throw new \RuntimeException("Failed to connect to {$this->host}:{$this->port}");
        }
    }

    public function call(Request $request)
    {
        $request->id = uniqid();
        $request->jsonrpc = '2.0';

        foreach ($this->middleware as $middleware) {
            $middleware->process($request);
        }

        $data = json_encode($request);
        $this->send($data);

        $response = new Response();
        $response->id = $request->id;
        $response->jsonrpc = '2.0';

        $data = $this->recv($this->callTimeout);
        $responseData = json_decode($data, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            throw new \RuntimeException("Invalid JSON response: " . json_last_error_msg());
        }

        $response->result = $responseData['result'] ?? null;
        $response->error = $responseData['error'] ?? null;

        return $response;
    }

    public function close()
    {
        $this->client->close();
    }

    protected function recv(float $timeout = -1)
    {
        $data = $this->client->recv($timeout);
        if ($data === false) {
            throw new \RuntimeException("Failed to receive data from server");
        }
        return $data;
    }

    protected function send(string $data)
    {
        if (!$this->client->send($data)) {
            throw new \RuntimeException("Failed to send data to server");
        }
    }
}
